import React from "react";
import Price from "./price";
import data4 from "./data4.json";
import Icons from "./icons";
import Info from "./info";

interface CarProps {
  id: number;
}

function Car(props: CarProps) {
  function calculateTimeDifference(startDate: Date, endDate: Date): string {
    const timeDiff = Math.abs(endDate.getTime() - startDate.getTime());

    const days = Math.floor(timeDiff / (1000 * 60 * 60 * 24));
    if (days > 0) {
      return `${days} day${days > 1 ? "s" : ""} ago`;
    }

    const hours = Math.floor(timeDiff / (1000 * 60 * 60));
    if (hours > 0) {
      return `${hours} hour${hours > 1 ? "s" : ""} ago`;
    }

    const minutes = Math.floor(timeDiff / (1000 * 60));
    if (minutes > 0) {
      return `${minutes} minute${minutes > 1 ? "s" : ""} ago`;
    }

    return "now";
  }

  const index: number = props.id;
  const car = data4.data.items.filter((item) => index === item.man_id);
  const link: string = `https://static.my.ge/myauto/photos/${car[0].photo}/thumbs/${car[0].car_id}_1.jpg?v=${car[0].photo_ver}`;
  return (
    <div className="card">
      <Price price={car[0].price} />
      <Icons />
      <Info
        gear={car[0].gear_type_id}
        engine_volume={car[0].engine_volume}
        fuel_type={car[0].fuel_type_id}
        run={car[0].car_run_km}
        steering={car[0].drive_type_id}
        // carBrand = {mwarmoebeli + modeli}
        carYear={car[0].prod_year}
        country={car[0].location_id}
        views={car[0].views}
        days={calculateTimeDifference(new Date(), car[0].order_date)}
      />
      <img className="img" src={link} />
    </div>
  );
}

export default Car;
