import React, { useEffect, useState } from "react";
import Price from "./price";
import Icons from "./icons";
import Info from "./info";
import moment from "moment";
import data1 from "./data1.json";

interface CarProps {
  id: number;
  selectCurrency: Function;
  currency: string;
  all_cars: any[];
}

interface CarData {
  car_id: number;
  model_id: number;
  man_id: number;
  price: number;
  price_usd: number;
  photo: string;
  photo_ver: number;
  prom_color: string;
  gear_type_id: number;
  engine_volume: number;
  fuel_type_id: number;
  car_run_km: number;
  drive_type_id: number;
  prod_year: number;
  location_id: number;
  views: number;
  order_date: string;
  customs_passed: boolean;
}

function Car(props: CarProps): JSX.Element | null {
  function calculateTimeDifference(startDate: string, endDate: string): string {
    const startMoment = moment(startDate);
    const endMoment = moment(endDate);
    const diff = endMoment.diff(startMoment);
    const duration = moment.duration(diff);

    if (duration.days() > 0) {
      return `${duration.days()} დღის${duration.days() > 1 ? "" : ""} წინ`;
    }

    if (duration.hours() > 0) {
      return `${duration.hours()} საათის${duration.hours() > 1 ? "" : ""} წინ`;
    }

    if (duration.minutes() > 0) {
      return `${duration.minutes()} წუთის${
        duration.minutes() > 1 ? "" : ""
      } წინ`;
    }

    return "ახლა";
  }

  const [car, setCar] = useState<CarData[]>([]);
  const [modelId, setModelId] = useState<number | undefined>(undefined);

  useEffect(() => {
    const foundCar = props.all_cars.filter(
      (item: CarData) => props.id === item.car_id
    );
    const carModelId = foundCar[0]?.model_id;
    console.log(carModelId);
    setCar(foundCar);
    setModelId(carModelId);
  }, [props.all_cars, props]);

  async function convertToName(modelId: number): Promise<string> {
    try {
      const response = await fetch(
        `https://api2.myauto.ge/ka/getManModels?man_id=${10}`
      );
      const data = await response.json();
      const filteredData = data.filter(
        (item: any) => item.man_id === car[0]?.man_id
      );
      return filteredData[0]?.model_name || "";
    } catch (error) {
      console.error(error);
      return "";
    }
  }

  if (!car || car.length === 0) return null;

  let carBrand = "";
  carBrand =
    carBrand +
    data1.filter((vehicle) => parseInt(vehicle.man_id) === car[0].man_id)[0]
      ?.man_name;
  carBrand = carBrand + " " + convertToName(modelId);

  if (carBrand.length > 22) carBrand = carBrand.substring(0, 22) + "...";

  const imageLink: string = `https://static.my.ge/myauto/photos/${car[0].photo}/thumbs/${car[0].car_id}_1.jpg?v=${car[0].photo_ver}`;

  return (
    <div className="card">
      <Price
        selectCurrency={props.selectCurrency}
        price={car[0].price}
        price_usd={car[0].price_usd}
        currency={props.currency}
      />
      <Icons />
      <Info
        prom_color={car[0].prom_color}
        gear={car[0].gear_type_id}
        engine_volume={car[0].engine_volume}
        fuel_type={car[0].fuel_type_id}
        run={car[0].car_run_km}
        steering={car[0].drive_type_id}
        carBrand={carBrand}
        carYear={car[0].prod_year}
        country={car[0].location_id}
        views={car[0].views}
        days={calculateTimeDifference(
          car[0].order_date,
          new Date().toDateString()
        )}
        passed={car[0].customs_passed}
      />
      <img className="img" src={imageLink} alt="Car" />
    </div>
  );
}

export default Car;
