import React from "react";
import Price from "./price";
import data4 from "./data4.json";
import Icons from "./icons";
import Info from "./info";
import moment from "moment";
import { DateTime, Duration } from "luxon";

interface CarProps {
  id: number;
}

function Car(props: CarProps) {
  function calculateTimeDifference(startDate: string, endDate: string): string {
    const startDateTime = DateTime.fromISO(startDate);
    const endDateTime = DateTime.fromISO(endDate);

    const diff = endDateTime.diff(startDateTime);
    const duration = Duration.fromMillis(diff.as("milliseconds"));

    if (duration.as("days") > 0) {
      return `${Math.floor(duration.as("days"))} day${
        Math.floor(duration.as("days")) > 1 ? "s" : ""
      } ago`;
    }

    if (duration.as("hours") > 0) {
      return `${Math.floor(duration.as("hours"))} hour${
        Math.floor(duration.as("hours")) > 1 ? "s" : ""
      } ago`;
    }

    if (duration.as("minutes") > 0) {
      return `${Math.floor(duration.as("minutes"))} minute${
        Math.floor(duration.as("minutes")) > 1 ? "s" : ""
      } ago`;
    }

    return "now";
  }

  const index: number = props.id;
  const car = data4.data.items.filter((item) => index === item.man_id);
  const link: string = `https://static.my.ge/myauto/photos/${car[0].photo}/thumbs/${car[0].car_id}_1.jpg?v=${car[0].photo_ver}`;
  return (
    <div className="card">
      <Price price={car[0].price} />
      <Icons />
      <Info
        gear={car[0].gear_type_id}
        engine_volume={car[0].engine_volume}
        fuel_type={car[0].fuel_type_id}
        run={car[0].car_run_km}
        steering={car[0].drive_type_id}
        // carBrand = {mwarmoebeli + modeli}
        carYear={car[0].prod_year}
        country={car[0].location_id}
        views={car[0].views}
        days={calculateTimeDifference(
          car[0].order_date.replace(" ", "T"),
          new Date().toDateString()
        )}
      />
      <img className="img" src={link} />
    </div>
  );
}

export default Car;
