import React from "react";
import Price from "./price";
import data4 from "./data4.json";
import Icons from "./icons";
import Info from "./info";

interface CarProps {
  id: number;
}

function Car(props: CarProps) {
  function getNumberOfDays(dateString: string) {
    // Parse the input date string
    const inputDate: Date = new Date(dateString);

    // Get the current date
    const currentDate: Date = new Date();

    // Calculate the difference in milliseconds
    const differenceInMilliseconds =
      currentDate.getMilliseconds() - inputDate.getMilliseconds();

    // Convert milliseconds to days
    const millisecondsInADay = 1000 * 60 * 60 * 24;
    const numberOfDays = Math.floor(
      differenceInMilliseconds / millisecondsInADay
    );

    return numberOfDays;
  }

  const index: number = props.id;
  const car = data4.data.items.filter((item) => index === item.man_id);
  const link: string = `https://static.my.ge/myauto/photos/${car[0].photo}/thumbs/${car[0].car_id}_1.jpg?v=${car[0].photo_ver}`;
  return (
    <div className="card">
      <Price price={car[0].price} />
      <Icons />
      <Info
        gear={car[0].gear_type_id}
        engine_volume={car[0].engine_volume}
        fuel_type={car[0].fuel_type_id}
        run={car[0].car_run_km}
        steering={car[0].drive_type_id}
        // carBrand = {mwarmoebeli + modeli}
        carYear={car[0].prod_year}
        country={car[0].location_id}
        views={car[0].views}
        days={getNumberOfDays(car[0].order_date)}
      />
      <img className="img" src={link} />
    </div>
  );
}

export default Car;
