import React, { useState } from "react";

interface PagesProps {
  page: number;
  setPage: Function;
}

function Pages(props: PagesProps) {
  const totalPages = 8; // Total number of pages
  const maxVisiblePages = 4; // Maximum number of visible pages

  const handlePageClick = (pageNumber: number) => {
    props.setPage(pageNumber);
  };

  const renderPageButtons = () => {
    const buttons = [];
    const startPage = Math.max(1, props.page - Math.floor(maxVisiblePages / 2));
    const endPage = Math.min(startPage + maxVisiblePages - 1, totalPages);

    for (let i = startPage; i <= endPage; i++) {
      const style: React.CSSProperties =
        props.page === i ? { color: "orangered" } : {};

      buttons.push(
        <button
          key={i}
          className={`page1`}
          style={style}
          onClick={() => handlePageClick(i)}
        >
          {i}
        </button>
      );
    }

    return buttons;
  };

  return <div className="pages">{renderPageButtons()}</div>;
}

export default Pages;
